mapper.update:
update goods_info
set visit_count = visit_count + 1
where id = #{goodsId}

mapper.select:
select id,category_id,category_name,pic_url,uid,nick_name,goods_price,goods_title,goods_content,visit_count, comment_count,create_time,update_time,goods_status,buy_time,old_degree from goods_info where goods_status = 0
and (goods_title like concat('%',#{condition.keyword},'%') or goods_content like concat('%',#{condition.keyword},'%'))
and category_id = #{condition.categoryId}
and create_time >= #{condition.publishTimeMin}
order by ${condition.sort} DESC
